// Remove the messages of the last frame for no dataloss mode
Object
    .values(gdjs.evtTools.mqtt.events)
    .forEach((event, index) => {
        if (Array.isArray(event.data))
            event.data.shift();
        else event.data = gdjs.evtTools.mqtt.empty;
    });

// Parse settings
const settings = eventsFunctionContext.getArgument("settings");
let settingsParsed;
if (settings.length > 2)
    try {
        settingsParsed = JSON.parse(settings);
    } catch (e) {
        settingsParsed = {}
        console.error("Invalid settings: ", settings, ", Error:", e);
    };

// Connect to broker
gdjs.evtTools.mqtt.connection = mqtt.connect(
    (eventsFunctionContext.getArgument("secure")
        ? "wss://"
        : "ws://")
    + eventsFunctionContext.getArgument("host"),
    settingsParsed
);

// Set up listeners
gdjs.evtTools.mqtt.connection.on("message", (channel, message) => {
    if (typeof gdjs.evtTools.mqtt.events[channel] === undefined) {
        gdjs.evtTools.mqtt.events[channel] = {
            loss: false,
            data: [message],
        };
        return;
    }

    const mqttEvent = gdjs.evtTools.mqtt.events[channel];
    if (mqttEvent.loss) {
        mqttEvent.data = message;
    } else {
        if (!Array.isArray(mqttEvent.data)) mqttEvent.data = [];
        mqttEvent.data.push(message);
    }
});

gdjs.evtTools.mqtt.connection.on("error", function (e) {
    gdjs.evtTools.mqtt.lastError = e;
    gdjs.evtTools.mqtt.hasError = true;
});

gdjs.evtTools.mqtt.connection.on("closed", function () {
    gdjs.evtTools.mqtt.disconnected = true;
});

if (gdjs.evtTools.mqtt.connection === null) return;
gdjs.evtTools.mqtt.connection.end(eventsFunctionContext.getArgument("force"));
gdjs.evtTools.mqtt.connection = null;

if (gdjs.evtTools.mqtt.connection === null) return;
gdjs.evtTools.mqtt.connection.publish(
    eventsFunctionContext.getArgument("topic"),
    eventsFunctionContext.getArgument("content"),
    { qos: eventsFunctionContext.getArgument("QoS"), retain: eventsFunctionContext.getArgument("retain") }
);

if (gdjs.evtTools.mqtt.connection === null) return;

const topic = eventsFunctionContext.getArgument("topic");
if (typeof gdjs.evtTools.mqtt.events[topic] === "undefined") gdjs.evtTools.mqtt.events[topic] = { data: eventsFunctionContext.getArgument("dataloss") ? "" : [] };
gdjs.evtTools.mqtt.events[topic].loss = eventsFunctionContext.getArgument("dataloss");

gdjs.evtTools.mqtt.connection.subscribe(
    topic,
    { qos: eventsFunctionContext.getArgument("QoS") }
);

const mqttEvent = gdjs.evtTools.mqtt.events[eventsFunctionContext.getArgument("topic")];
if (typeof mqttEvent === "undefined" || typeof mqttEvent.data === "undefined") return;
eventsFunctionContext.returnValue =
    Array.isArray(mqttEvent.data)
        ? mqttEvent.data.length !== 0
        : mqttEvent.data !== gdjs.evtTools.mqtt.empty;

const mqttEvent = gdjs.evtTools.mqtt.events[eventsFunctionContext.getArgument("topic")];
if (typeof mqttEvent === "undefined") return;
eventsFunctionContext.returnValue = Array.isArray(mqttEvent.data)
    ? mqttEvent.data[0]
    : mqttEvent.data;

const mqttEvent = gdjs.evtTools.mqtt.events[eventsFunctionContext.getArgument("topic")];
if (typeof mqttEvent === "undefined") return;
if (Array.isArray(mqttEvent.data)) {
    mqttEvent.data.shift();
}
else {
    mqttEvent.data = null;
}

const stickerBehaviorName = eventsFunctionContext.getBehaviorName("Behavior");
/** @type {Hashtable<gdjs.RuntimeObject[]>} */
const stickerObjectsLists = eventsFunctionContext.getObjectsLists("Object");
/** @type {Hashtable<gdjs.RuntimeObject[]>} */
const basisObjectsLists = eventsFunctionContext.getObjectsLists("BasisObject");

eventsFunctionContext.returnValue = gdjs.evtTools.object.twoListsTest(
  (stickerObject, basisObject, stickerBehaviorName) => {
    const behavior = stickerObject.getBehavior(stickerBehaviorName);
    return behavior.basisObject === basisObject;
  },
  stickerObjectsLists,
  basisObjectsLists,
  false,
  stickerBehaviorName
);

const behaviorName = eventsFunctionContext.getBehaviorName("Behavior");
const object = objects[0];
const behavior = object.getBehavior(behaviorName);

// Set up the scene sticker objects list - if not done already.
if (!runtimeScene.__allStickers)
    runtimeScene.__allStickers = new Set();

// Set up the behavior extra methods - if not done already.
const prototype = Object.getPrototypeOf(behavior);
if (!prototype.updateRelativeCoordinates) {
    // Unstick from deleted objects.
    gdjs.registerObjectDeletedFromSceneCallback(function (runtimeScene, deletedObject) {
        if (!runtimeScene.__allStickers) return;

        for (const sticker of runtimeScene.__allStickers) {
            if (sticker.basisObject === deletedObject) {
                sticker.basisObject = null;
            }
        }
    });

    /**
     * Update the coordinates in the basisObject basis.
     * 
     * It uses the basisObject coordinates from the previous frame.
     * This way, the sticker can move relatively to it and still
     * follow basisObject.
     * 
     * @param {gdjs.RuntimeObject} basisObject
